package com.hose.mall.search;import java.io.Serializable;/** * 酒店适配 * * @author wangmi wangmi@hosecloud.com */public class DetailAdapterVo implements Serializable {    private static final long serialVersionUID = 6277170362855031261L;    public DetailAdapterVo() {        super();    }    /**     * 自增主键     */    private Long id;    /**     * 酒店编号（本地酒店主键）     */    private String hotelNo;    /**     * 酒店平台编号     */    private String hotelPltNo;    /**     * 渠道平台（ELONG--艺龙  JINJIANG--锦江  HUAZHU--华住  VIENNA--维也纳  PLATENO--铂涛  SUPER--速8）     */    private String platform;    /**     * 平台酒店状态     */    private Boolean pltStatus;    /**     * 平台城市代码     */    private String pltGeo;    /**     * 渠道产品代码     */    private String product;    /**     * 渠道平台最低价格     */    private Double lowRate;    public Long getId() {        return this.id;    }    public void setId(Long id) {        this.id = id;    }    public String getHotelNo() {        return this.hotelNo;    }    public void setHotelNo(String hotelNo) {        this.hotelNo = hotelNo;    }    public String getHotelPltNo() {        return this.hotelPltNo;    }    public void setHotelPltNo(String hotelPltNo) {        this.hotelPltNo = hotelPltNo;    }    public String getPlatform() {        return this.platform;    }    public void setPlatform(String platform) {        this.platform = platform;    }    public Boolean getPltStatus() {        return this.pltStatus;    }    public void setPltStatus(Boolean pltStatus) {        this.pltStatus = pltStatus;    }    public String getPltGeo() {        return this.pltGeo;    }    public void setPltGeo(String pltGeo) {        this.pltGeo = pltGeo;    }    public String getProduct() {        return this.product;    }    public void setProduct(String product) {        this.product = product;    }    public Double getLowRate() {        return this.lowRate;    }    public void setLowRate(Double lowRate) {        this.lowRate = lowRate;    }    @Override    public String toString() {        return "{\"id\":" + this.id + ",\"hotelNo\":\"" + this.hotelNo + "\",\"hotelPltNo\":\"" + this.hotelPltNo + "\",\"platform\":\"" + this.platform + "\",\"pltStatus\":" + this.pltStatus + ",\"pltGeo\":\"" + this.pltGeo + "\",\"product\":\"" + this.product + "\",\"lowRate\":" + this.lowRate + "}";    }}